                                       <div class="bt_bb_column col-xxl-1 col-xl-1 bt_bb_vertical_align_top bt_bb_align_left bt_bb_padding_normal" data-width="1" data-bt-override-class="{}">
                                          <div class="bt_bb_column_content">
                                             <div class="bt_bb_column_content_inner"></div>
