                                       <div class="bt_bb_column col-xxl-10 col-xl-10 bt_bb_vertical_align_top bt_bb_align_left bt_bb_padding_double bt_bb_animation_fade_in animate animated" data-width="10" data-bt-override-class="{}">
                                          <div class="bt_bb_column_content">
                                             <div class="bt_bb_column_content_inner">
                                                <div class="bt_bb_accordion bt_bb_style_simple bt_bb_shape_square bt_bb_icon_size_small">
